export const initDB = async () => {
  if (db) return;

  try {
    // @ts-ignore - initSqlJs is loaded globally via CDN in index.html
    if (!window.initSqlJs) {
        throw new Error("SQL.js not loaded");
    }
    
    // Locate the WASM file relative to the CDN
    // @ts-ignore
    SQL = await window.initSqlJs({
      locateFile: (file: string) => `https://cdnjs.cloudflare.com/ajax/libs/sql.js/1.8.0/${file}`
    });

    db = new SQL.Database();
    
    // Run seed data
    db.run(SEED_QUERY);
    console.log("Database initialized with seed data");

  } catch (err) {
    console.error("Failed to initialize database", err);
    throw err;
  }
};

export const resetDB = () => {
    if (!db) return;
    try {
        db.close();
        db = new SQL.Database();
        db.run(SEED_QUERY);
        console.log("Database reset to initial state");
    } catch (e) {
        console.error("Error resetting DB", e);
    }
}

export const executeSQL = (query: string) => {
  if (!db) {
    throw new Error("Database not initialized");
  }

  try {
    // sql.js .exec returns an array of result sets. We usually want the first one.
    // Structure: [{ columns: ['id', 'name'], values: [[1, 'Bob'], [2, 'Alice']] }]
    const results = db.exec(query);

    if (results.length === 0) {
      return { columns: [], values: [] };
    }

    return results[0];
  } catch (err: any) {
    throw new Error(err.message);
  }
};

export interface TableInfo {
    name: string;
    columns: { name: string, type: string }[];
}

export const getSchemaInfo = (): TableInfo[] => {
    if (!db) return [];
    try {
        // Get table names
        const tablesRes = db.exec("SELECT name FROM sqlite_master WHERE type='table' AND name NOT LIKE 'sqlite_%'");
        if (!tablesRes.length) return [];
        
        const tables = tablesRes[0].values.map((r: any) => r[0]);
        
        return tables.map((tableName: string) => {
            const colsRes = db.exec(`PRAGMA table_info(${tableName})`);
            const columns = colsRes.length ? colsRes[0].values.map((c: any) => ({
                name: c[1],
                type: c[2]
            })) : [];
            return { name: tableName, columns };
        });
    } catch (e) {
        console.error("Error fetching schema", e);
        return [];
    }
};

export const getTableSchema = () => {
    if(!db) return [];
    // Helper to help the AI know what tables exist
    const res = db.exec("SELECT name, sql FROM sqlite_master WHERE type='table'");
    if(res.length > 0) {
        return res[0].values.map((v: any[]) => `${v[0]}`).join(", ");
    }
    return "";
}
